SET sql-client.execution.result-mode=TABLEAU;
CREATE CATALOG paimon_catalog WITH ('type' = 'paimon', 'warehouse' = 'file:///opt/flink/storage/paimon_warehouse');
USE CATALOG paimon_catalog;
USE testdb;
CREATE TABLE IF NOT EXISTS users (id INT, name STRING, email STRING, age INT, created_at TIMESTAMP, updated_at TIMESTAMP, PRIMARY KEY (id) NOT ENFORCED) WITH ('connector' = 'paimon', 'file.format' = 'parquet', 'write-mode' = 'append-only');
CREATE TABLE IF NOT EXISTS orders (id INT, user_id INT, product_name STRING, quantity INT, price DECIMAL(10,2), order_date TIMESTAMP, PRIMARY KEY (id) NOT ENFORCED) WITH ('connector' = 'paimon', 'file.format' = 'parquet', 'write-mode' = 'append-only');
